float Bubble(vec2 origin, float r, float blur)
{

    float d = length(origin);
    float c = smoothstep(r, r - blur, d);

    return c;
}


float SuperBubble(vec2 origin, float r){
    float c = Bubble(origin, r, .03);

    c += Bubble(origin - vec2(r * .25), r * 0.7, .2);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Current origin
    uv -= vec2(.5, .3);

    // Not an ellipse anymore
    uv.x *= iResolution.x/iResolution.y;

    float c = SuperBubble(uv, .5);

    vec3 col = vec3(0.9 ,1, 9.) * c * 0.5;

    fragColor = vec4(col, 1.0);
}
