void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pos = fragCoord.xy; //coordenadas de la superficie
    pos.x /= iResolution.x;
    pos.y /= iResolution.y;
    vec4 solidColor = vec4(0, 0, 0, 1);// color sólido negro
    if(pos.x > 0.5)//si la coordenada en x es mayor a la mitad (0.5), entonces volverlo 1 
    //en el canal r (color rojo)
    {
        solidColor.r = 1.0;
    }
    fragColor = solidColor; //enviar el color
}
