float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}


float func( vec2 uv)
{
uv = floor(99. * uv) / 99.;

vec2 dir = uv - 0.5;
dir = fract(5. * dir + 0.5) - 0.5;
float theta = atan(dir.y,dir.x);

float k = 2. + 2. * cos(sin(theta) + iTime * (0.2 + abs(random(floor(uv * 50.)))));
float d =  0.2 *pow(abs(dir.x) * k, k ) +pow(abs(dir.y) * k,k);

return smoothstep(d,0.0,2.9 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec2 uv = fragCoord/iResolution.y;
  uv -= vec2(0.02 * iTime,-0.08 * iTime);

vec3 col = vec3(0.05,min(func(uv.yx),(1.- func(uv))),0.1);
    // Output to screen
    fragColor = vec4(col,1.0);
}
