void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 UV = fragCoord / vec2(iResolution.x, iResolution.y);
    vec2 UV2 = UV;

    float Speed1 = 0.1;
    float ModStrength = 0.01;

    UV.x += iTime * Speed1;

    float UVMod = texture(iChannel1, UV).x * ModStrength;
    UV2 += UVMod;


   float Speed = 5.0;
    float Time = (sin(iTime * Speed) * 0.5) + 0.5;

    vec4 Color = vec4(0.2, 0.0, 0.0, 0.5) * Time;


     vec2 Mouse = iMouse.xy / iResolution.xy;

     vec4 Color1 = vec4(Mouse.x, Mouse.y, 0.0, 1.0);


    vec4 Tex = vec4(texture(iChannel0, UV2));

    fragColor = Tex + Color + Color1;
}
