const float sSize = 12.0;
const float sPos = 2.0;
const float ePos = 1.0 + sPos;
const float Count = 10.0;
const vec3 sCol = vec3(1.0, 0.9, 0.0);
const vec3 eCol = vec3(1.0, 0.0, 0.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float flag = 0.0;

    for(float n = 1.0; n <= Count; n++) {
        float t = fract(iTime + n / Count);
        float rand = fract(sin(dot(vec2(fract(ceil(iTime + n / Count) / n), n / Count), vec2(12.9898, 78.233))) * 43758.5453123);
        float rx = rand - 1.0 - (1.0 / 2.0) + mod(n, 3.0);

        vec2 pos = fragCoord / iResolution.y * 2.0;
        vec2 cpos = vec2(sin(t * 5.0 + sin(rand) * 20.0) / 10.0 + rx / 5.0, sPos - t * ePos);
        vec2 fpos = vec2(iResolution.x / iResolution.y, 1.0);

        vec2 rd = pos + vec2(cpos.x * ((pos.y + 1.0) / 2.0), cpos.y - 0.5) - fpos;
        rd *= sSize / (1.0 - t) * (pos.y * pos.y / 20.0 + 0.2);
        rd.x /= 1.0 - pos.y + rand * 0.5;
        rd.x += (1.0 - pos.y) * (rand * 2.0 - 1.0) * 0.5;

        flag = flag + step(length(rd), 1.0);
    }

    vec3 color = mix(sCol, eCol, fragCoord.y / iResolution.y);
    fragColor = vec4(color * step(1.0, flag), 1.0);
}
