void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float size = 50.0;
    float intensity = 20.0;
    float pattern = sin(fragCoord.x / size) * sin(fragCoord.y / size);
    float variation = sin(iTime * 0.1) * intensity ;
    float mixed = sin(pattern * variation);
    vec3 col = vec3(0, 0, mixed);

    // Output to screen
    fragColor = vec4(col,1.0);
}
