#define RED vec4(0.8, 0.2, 0.2, 1.0)
#define RATIO (iResolution.y/iResolution.x)

vec4 flag(vec2 uv, float t)
{
    float extra_offset = clamp(0.0, 1.0, abs(pow(t, 10.)) * 1.2) * 2000.0 * RATIO;
    vec2 bpos = vec2(0.0, -extra_offset);
    float dist = distance(bpos, uv) - (max(0.3, extra_offset));

    return mix(RED, vec4(1.0), step(0.0, dist));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy*2.0 - 1.0;
	uv.y *= RATIO;
    float t = sin(sin(uv.y * 10. + iTime) * 1. + length(uv.x + uv.y + vec2(2.0)) * 6.0 + iTime);
    fragColor = flag(uv, sin(iTime) / 2.0 + 0.5);
}
