// Public Domain, feel free to use whereever and however you want.

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    const float offset = 0.01;
    fragColor = vec4(texture(iChannel0, uv+vec2(offset, 0.0)).r, texture(iChannel0, uv+vec2(-offset, 0.0)).g, texture(iChannel0, uv).b, 1.0);
}
