vec3 onelight(vec2 uv, float startTime) //one light (m=imouse.y)
{
    float t = iTime +startTime;
    vec3 col;
    const float pi = 3.141592653589793238;
    uv.x+=.5*cos(t);
    uv.y+=.1*sin(t+iTime);
    float r = .05*sin(t)+.15;
    float a = length(uv);
    col += r/a; 
    col = .05*vec3(col.r*sin(t),col.g*sin(t+2.*pi/3.),col.b*sin(t+4.*pi/3.));
    //col *= .5; //uncomment this line and comment 11th line to monochrome lights
    return col;
}


vec3 nlights(vec3 col, vec2 uv, float max) //sum of [max] lights
{
    const float pi = 3.141592653589793238;
    float j=1.;
    while(j<max+1.)
    {
       col+=onelight(uv, j*2.*pi/float(max));
       j++;
    }
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord  - .5 * iResolution.xy ) / iResolution.y;
    vec3 col = vec3(0.);
    float a = length(uv);
    fragColor = vec4(nlights(col, uv, 10./*<<< change amount here [float]*/)+col, 1.);
}
