void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1) and centered
    vec2 uv = (fragCoord.xy - iResolution.xy / 2.) / iResolution.y;


    vec2 Center = vec2(0, 0);
    float Size = .5;
    float SmoothStepAMT = .01;


    //fragColor = vec4(1,1,1, 1);

    //if (distance(uv, Center) * 1./Size < .5)
    //    fragColor = vec4(0,0,0,1);

    //this line provided by FabriceNeyret2; Edited
    fragColor = vec4(smoothstep (Size, Size +SmoothStepAMT +2. /iResolution.y, distance(uv, Center) * 1. /Size));
}
