// minimalist variant of  https://shadertoy.com/view/wtsczN

void mainImage(out vec4 O, vec2 u) {   

    vec2  R = iResolution.xy,                               
          U = u+u - R;  

    float f = 2.307, // Golden Angle 2.4 ok too
          s = 15./R.y,
         PI = 3.14, L = 2.*PI/f,
          a = atan(-U.y,U.x), l = length(U)*s, 
         x1 =  a / f,
         x2 = l*l,
          k = round((x2-x1)/L ),
          x = round( x1 + L*k );

    O = vec4( x > 2e2 ? 0. :
              1.5-.5* length( U - sqrt(x) *cos ( x * f  + vec2(0,PI/2.)) / s )  );  
}
