float sphere(vec3 p) {
    return length(p) - 2.0;
}


float map(vec3 p) {
    vec3 c = vec3(20.0);
    return sphere(mod(p + 0.5 * c, c) - 0.5 * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(gl_FragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 rd=normalize(vec3(uv,-1)); // I prefer OpenGL coordinate!
    vec3 p=vec3(5.0,0,-10.0 * iTime);
    float d=1.,i;
    for(;++i<99.&&d>.001;)p+=rd*(d=map(p));
    if(d<.001)fragColor+=3./i;
}
