void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 bkg = vec4(1.0);
    vec4 col = vec4(0.78, 0.1, 0.22, 1.0);
    vec2 s = vec2(0.7, 0.72);
    vec2 w = vec2(3.0)/iResolution.xy;
    if ( /* inner */
      uv.x > (1.-s.x)+w.x && uv.x < s.x-w.x &&
      uv.y > (1.-s.y)+w.y && uv.y < s.y-w.y) fragColor = bkg;
    else if ( /* border */
      uv.x > (1.-s.x)-w.x && uv.x < s.x+w.x &&
      uv.y > (1.-s.y)-w.y && uv.y < s.y+w.y) fragColor = col;
    else /* outter */ fragColor = bkg;
}
