// Golfed by https://www.shadertoy.com/user/Xor

void mainImage (out vec4 O, vec2 I)
{
    O -= O;

    for(int i=0; i++<36;)
    {
        vec2 r = iResolution.xy,
             c = (I+I-r-1.+vec2(i%6,i/6)/3.) / r.y*8.-.5,
             p = fract(c)-.5;

        O += step(dot(p,p),.23) * fract(atan(p.x,p.y)/6.3
             + tan(length(ceil(c))*.2-iTime)*.1)/36.;
    }
}

