float Circle(vec2 uv, vec2 position, float radius, float blur)
{
    uv -= position;
    return smoothstep(radius, radius - blur, length(uv)); 
}

float SmileyFace(vec2 uv, vec2 position ,float size)
{
    float mask = 0.0;

    uv -= position;
    uv /= size;

    mask = Circle( uv, vec2(0.0, 0.0), 0.4, 0.005); 
    mask -= Circle( uv, vec2(0.2, 0.1), 0.1, 0.005); 
    mask -= Circle( uv, vec2(-0.2 , 0.1), 0.1, 0.005); 
    mask += Circle( uv, vec2(0.0 , -0.1), 0.17, 0.005); 
    mask -= Circle( uv, vec2(0.0 , -0.2), 0.12,0.005); 
    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -=0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 clr1 = vec3(0,0,0);
    float mask = SmileyFace(uv/sin(iTime),-vec2(cos(iTime)), 0.5);
    clr1 = vec3(mask);
    fragColor = vec4(clr1,1.0); 

    vec3 clr2 = vec3(0,1.0,1.0);
    mask += SmileyFace(-uv/cos(iTime),vec2(sin(iTime)/2.), 0.5);
    clr2 *= vec3(mask);
    fragColor += vec4(clr2,1.0);

}
