void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float dotResolution = 50.;
  vec2 uv = fragCoord / iResolution.xy;
  vec4 color = texture(iChannel0, uv);

  // RGB-to-CMYK conversion
  vec4 cmyk;
  cmyk.xyz = 1.0 - color.rgb;
  cmyk.w = min(cmyk.x, min(cmyk.y, cmyk.z));
  cmyk.xyz -= cmyk.w;

  // Build dot grid for each CMYK component
  vec2 cUV = dotResolution*mat2(0.966, -0.259, 0.259, 0.966)*uv;
  vec2 cUV2 = 2.0*fract(cUV)-1.0;
  float cR = sqrt(cmyk.x);
  float c = smoothstep(cR + 0.1, cR - 0.1, length(cUV2));

  vec2 mUV = dotResolution*mat2(0.966, 0.259, -0.259, 0.966)*uv;
  vec2 mUV2 = 2.0*fract(mUV)-1.0;
  float mR = sqrt(cmyk.y);
  float m = smoothstep(mR + 0.1, mR - 0.1, length(mUV2));

  vec2 yUV = dotResolution*uv;
  vec2 yUV2 = 2.0*fract(yUV)-1.0;
  float yR = sqrt(cmyk.z);
  float y = smoothstep(yR + 0.1, yR - 0.1, length(yUV2));

  vec2 kUV = dotResolution*mat2(0.707, -0.707, 0.707, 0.707)*uv;
  vec2 kUV2 = 2.0*fract(kUV)-1.0;
  float kR = sqrt(cmyk.w);
  float k = smoothstep(kR + 0.1, kR - 0.1, length(kUV2));

  vec3 rgb = 1. - vec3(c, m, y);
  rgb = mix(rgb, vec3(0., 0., 0.), k);
  fragColor = vec4(rgb, 1.0);
}
