void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = uv*4.;

    float c = 0.;

    if(uv.x < 0.5){
        if(uv.y < 0.5)
            c = abs(sin(uv2.x + iTime));// left bottom
        else{
            uv2 *= 300.;
            c = abs(sin(uv2.x / iTime));// left top
        }
    }else if(uv.x > 0.5){
        if(uv.y < 0.5)
            c = abs(sin(uv2.x - iTime));// right bottom
        else
            c = abs(sin(uv.x * iTime));// right top
    }

    fragColor.r = c;
}
