void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(1.0,1.0,1.0);

    float heightRatio = 0.05;//波浪高度
    float angleSpeed = 10.0;//角速度，周期
    float speed = 10.0;//移动速度
    float offset = 0.5;//偏移距离 不偏移的话就在屏幕中间了
    float centerY = heightRatio * sin((angleSpeed * uv.x) + speed * iTime) + offset;
    if(uv.y > centerY) {
        col = vec3(0.0, 0.0, 0.0);
    }
    fragColor = vec4(col,1.0);
}
