// Heart Eyes Emoji

vec2 rot(vec2 xy, float a) { return xy*mat2(cos(a), -sin(a), sin(a), cos(a));}
//float heart(vec2 uv){ return smoothstep(.405,.375,length(vec2(uv.x*1.5,uv.y*sign(uv.x)*2.-uv.x))); }
float heart(vec2 uv){ return smoothstep(.405,.375,length(vec2(1.5,uv.y/abs(uv.x)*2.-1.)*uv.x)); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2     uv = fragCoord/iResolution.yy; 

    float woohoo = 1. - .14*abs(sin(clamp(mod(iTime*2.,5.),0.,1.)*3.14*2.));

    //  mouth
    float shading = uv.y*3.+.1;
    float mouth = smoothstep(.225,.22,distance(uv,vec2(.5,.42)));
    mouth -= 1.5-shading*smoothstep(.6,.605,distance(uv,vec2(.5,.92)));
    mouth=max(0.,mouth);

    // face
    shading = smoothstep(1.,.5,length(uv-.5)*1.8);
    float face = smoothstep(.405,.4,distance(uv,vec2(.5))) * shading;
    float faceshimmer = smoothstep(.4,.2,distance(uv,vec2(.5,.55)))*uv.y*uv.y;

    // left eye
    uv = (rot(uv,-3.14/12.)*2. - vec2(.92, 1.)) * woohoo;
    float eye = heart(uv);

    // right eye
    uv = fragCoord/iResolution.yy;
    uv = (rot(uv,3.14/12.)*2. - vec2(1.,1.52)) * woohoo;
    eye += heart(uv);

    // Output to screen
    fragColor = vec4(vec3(face,face*.8,0.)+faceshimmer+eye*vec3(-.5,-1.5,-.5)-mouth,1.);  
}
