void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = ( 2.0 * fragCoord.xy - iResolution.xy ) / iResolution.y;

    float plot = 0.0;
    for ( int i = -10; i <= 10; i ++ ) {
        vec2 pt = p + vec2( i, 0.0 ) / 10.0 * 4.0 / iResolution.y;

        pt.y = fs( pt.x / 60.0 + iTime ) - 0.5;

        plot = max( plot, linearstep( 4.0 / iResolution.y, 2.0 / iResolution.y, length( pt - p ) ) );
    }


    fragColor = vec4( plot, 0.0, 0.0, 1.0 );
}

vec2 mainSound( int samp, float time ) {
    return vec2( 0.1 * ( 2.0 * fs( time ) - 1.0 ) );
}

#define saturate(i) clamp(i, 0.,1.)
#define linearstep(a,b,x) saturate(((x)-(a))/((b)-(a)))

float fs( float t ) {
    return fract( sin( t * 114.514 ) * 1919.810 );
}
