void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(texture(iChannel0, uv).xyz*col,1.0);
}

float ll(vec2 uv, vec2 c0, vec2 c1, float t, float f, float a){
    return log(.001+(cos(t+length(uv-c0)*f)+1.)*(cos(t+length(uv-c1)*f)+1.))*0.01*a;
}

float bnc(float t){
    return abs(fract(t)-0.5)*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float my = iMouse.y; if(my <= .0) my = 70.;

    vec2 uv = fragCoord/iResolution.xy*(3.*my/iResolution.y);

    float t = iTime*.8;

    float diff= .5+sin(t)*8.;

    vec2 b0 = vec2(bnc(t*0.04), bnc(t*.011));
    vec2 b1 = vec2(bnc(2.+t*.013), bnc(2.+t*.09));

    float a = ll(uv, b0, b1, t, 14., 1.2);
    float a2 = ll(uv+.4, b0, b1, t, 15., 1.);
    float a3 = ll(uv-.4, b0, b1, t, 16., 1.);

    float f = (a2- diff*a +a3)/.2;

    float c = (.1-(6.*f));

    vec3 cc = vec3(1.-c*c);

    cc /= mix(cc, 1./texture(iChannel0, .99*fragCoord/iResolution.xy).xyz, .07)*(.5+.5*cos(.001*uv.xyx*iTime*vec3(1.,2.,3.)));

    fragColor = vec4(1.-cc,1.0);
}
