#define sat(a) clamp(a, 0., 1.)
float lenny(vec2 v)
{
    return abs(v.x)+abs(v.y);
}

mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c,-s,s,c);}

float _sqr(vec2 p, vec2 s)
{
    vec2 l = abs(p)-s;
    return max(l.x, l.y);
}

float border(vec2 uv)
{
    uv -= vec2(0.,.02);
    float body = _sqr(uv, vec2(.3,.2));
    body = max(body, uv.x-uv.y-.475);
    return abs(body)-0.0001;
}

vec3 rdr2(vec2 uv)
{

    vec3 col;
    //return texture(iChannel0, (uv-vec2(.5))*vec2(iResolution.x)/iResolution.xy).xyz;
    float cnt = 40.;
    for (float i = 0.; i < cnt; ++i)
    {
        // camera version
        //float pic = pow(texture(iChannel0, vec2(uv.x*.8+.5, 19.*i/1600.-iTime*.501+.3)).x, 2.);
        float pic = pow(texture(iChannel0, vec2(uv.x*.25, 1.*i/1600.-iTime*.01)).y, 1.5);
        pic *= sat(.5+abs(uv.x*2.));
        pic += 2.*pow(texture(iChannel1, vec2((uv.x+.5),0.)).x,.5)*(1.-sat(i/cnt));
        float lvl = abs(uv.y-.01*(i-cnt/2.)-0.05*pic)-0.001;
        lvl = max(lvl, abs(uv.x)-.25);
        col = mix(col, mix(vec3(0.071,0.769,0.627), vec3(1.000,0.918,0.502),sat(pic*2.-1.5)), (1.-sat(i/cnt))*(1.-sat(lvl*400.*(1.-sat(max(i/cnt, .1))))));
    }
    col = mix(col, col.yxz, sat((uv.x+.5)*2.));

    float strips = max(sin((uv.x-uv.y)*100.+iTime*2.), -(abs(uv.x)-.45));
    vec3 stripsCol = mix(vec3(0.071,0.769,0.627), vec3(1.000,0.918,0.502), sat(sin(uv.y*15.)*.5+.5));
    col = mix(col, stripsCol, 1.-sat(strips*iResolution.x));

    col += stripsCol * pow(1.-sat(length(uv)),5.)*.5;

    col = mix(col, stripsCol, 1.-sat(border(uv)*400.));
    return col;
}

vec3 rdr(vec2 uv)
{
    vec2 dir = normalize(vec2(1.));
    float str = 0.01*texture(iChannel1, vec2(0.9,0.)).x;
    vec3 col;
    col.r = rdr2(uv+dir*str).r;
    col.g = rdr2(uv).g;
    col.b = rdr2(uv-dir*str).b;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;
    float stp = 0.02;
    vec2 uv2 = floor(uv/stp)*stp;
    vec3 col = rdr(uv);
    col += rdr(uv2)*(1.-sat(length(uv2)))*.25;
    col = pow(col, vec3(.85));
    fragColor = vec4(col,1.0);
}
