void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    vec3 color = vec3(0.0);
    st += vec2(0.1,0.1);
  	st *= 8.;
    vec2 pos = vec2(0.5,0.5)-st;

    float r = length(pos)/3.976;
    float a = atan(pos.y,pos.x);

    float satan = cos(a*3.);
    satan = abs(cos(a*10.));
    satan *= abs(cos(a*5.))*0.612*(sin(iTime*13.)*0.4+0.9);
    satan /= abs(cos(a*12.)*sin(a*3.))*.8+.1;
    satan /= smoothstep(-.5,2., cos(a*8.))*0.2+1.45;

    color = vec3( 1.15-smoothstep(satan,satan+0.9,r) );

    fragColor = vec4(color, 1.);
}
