void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    const float rate = 0.1;
    float sTime = iTime * rate;

    const float sBase = 3.0;
    const float sRange = 5.0;
    const float tBase = 0.6;
    const float tRange = 0.5;

    vec3 sW0 = 0.5 + 0.5 * vec3(
        cos(1.0*sTime),
        cos(1.1*sTime),
        cos(1.2*sTime));

    vec3 sW1 = 0.5 - 0.5 * vec3(
        cos(1.05*sTime),
        cos(1.15*sTime),
        cos(1.25*sTime));

    vec3 tW0 = 0.5 + 0.5 * vec3(
        cos(0.9*sTime),
        cos(1.8*sTime),
        cos(1.7*sTime));

    vec3 tW1 = 0.5 - 0.5 * vec3(
        cos(0.95*sTime),
        cos(0.85*sTime),
        cos(0.75*sTime));

    vec3 s0 = sBase + sRange * sW0;
    vec3 s1 = sBase + sRange * sW1;
    vec3 t0 = tBase + tRange * tW0;
    vec3 t1 = tBase + tRange * tW1;


    // Time varying pixel color
    vec3 w = 0.5 + 0.25 * (cos(s0*(uv.yyy - 1.0 + t0)) + cos(s1*(uv.yyy - 1.0 + t1)));
//    float wG = cos(1.2*iTime+10.0*uv.y);
//    float wB = cos(-0.8*iTime+5.0*uv.y);

    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

//    vec3 col = 0.5 + 0.5 * vec3(wR, wR, wR);

    // Output to screen
    fragColor = vec4(w.xyz,1.0);
}
