float random(vec2 st){
    return fract(sin(dot(st,vec2(78.233,12.988))) * 5462.543);
}

float noise(vec2 st){
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.-2.*f);

    float a = random(i);
    float b = random(i + vec2(1.,0));
    float c = random(i + vec2(0,1.));
    float d = random(i + vec2(1.,1.));

    float rand = mix(mix(a,b,u.x),
                     mix(c,d,u.x),u.y);
    return rand;

}

float fbm(vec2 st){
    int octaves = 8;
    float rand = 0.;
    float amp = .5;
    float freq = 1.;
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    float shift = 100.;
    for(int i = 0; i < octaves; i++)
    {
        rand += amp * noise(st * freq);
        amp *= .5;
        st = rot * st * 2.0 + shift;
    }
    return rand;
}

float image(vec2 uv)
{
    uv.x -= .5;
    uv = uv ;
    // Time varying pixel color
    float rand = fbm(uv);
    float rand2 = fbm(uv *3.1415926 );
    rand = fbm(vec2(rand,rand2) * 3.14159 + .9 * (iTime * .2 + 2.4));

    rand *= 1.4;
    rand = smoothstep(.3, .7, rand);
    rand = pow(rand,8.);
    return rand;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord )/iResolution.y;
    vec2 uv2 = (fragCoord + vec2(1.))/iResolution.y;
    float rand = image(uv);
    float rand2 = image(uv2);
    float gradient = clamp(0.0,1.,rand - rand2);
    rand /= gradient;
    rand = pow(rand/200., .1);
    rand -= .4;

    vec3 col = vec3(0.);
    col += rand;


    // Output to screen
    fragColor = vec4(col,1.0);
}
