// variant of https://shadertoy.com/view/fsf3WX

#define OPAQUE 1

void mainImage( out vec4 O, vec2 U )
{
    O-=O;
    vec2 R = iResolution.xy, S = vec2(.5,.866),
         V = vec2( U.x, dot(U,S) ), A;
    vec3 I0;
    float Z = pow(3.,6.), a = Z/R.y, z;
    V /= R.y; R = vec2(Z), V *= Z;
    int i, n = int(log2(R.x));


    for ( z = 0.; z <= R.y; z++ ) {                   // slices along depth
        ivec3 I = ivec3 ( V -z , z ) , F; I0=vec3(I); // I = 3D location
        A = abs( vec2(I) - R.y/2. );
        if ( max(A.x,A.y) < R.y/2. )
        for ( i = 0; i <= n; i++, I/=3 ) {            // inspect ternary bits
            F = 1-abs(I%3-1);                         // iseq(I,1)
         // F = ivec3(equal( I%3, ivec3(1) ));        // iseq(I,1)
            if ( F.x+F.y+F.z > 1 ) break;             // at least 2 bits at 1: exit
          }
#if OPAQUE
        if ( i == n+1 ) break;                        // in Sierpinsky

#else
        if ( i == n+1 )  O += vec4( i == n+1 );       // in Sierpinsky : optical depth++
#endif
    }

#if OPAQUE
    if ( i == n+1 ) {                                 // in Sierpinsky â†’ paint
        vec3 A = abs( I0/Z*2. - 1. );
        i = int(iTime-1.) % 5;
        O = i==0 ? vec4( exp(-z*4./R.y) )             // AO 
          : i==1 ? vec4(1.-abs( I0/Z - .5 ), 1)       // color schemes
          : i==2 ? vec4( exp(-(1.-max(A.x,max(A.y,A.z)))*2.) )
          : i==4 ? vec4( exp(-A*2.) , 1)
          :        vec4( exp(-(1.-A)*2.) , 1);
    }
#else
    O = 1.-exp(-O/40.);                               // optical depth to opacity
#endif
}
