void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 9.* ( u+u - R ) / R.y;
         U += vec2(U.y,-U);                    // scaling and tilt

    float x = fract(U.x -=  sin(.6*U.y) )-.5,  // woobly bands
          y = .3*sin(12.*U.y)                  // high freq 
            * (1.-texture(iChannel0, u/R).r);  // modulation

    O = vec4( abs(x-y) / fwidth(U.x - y) );    // antialiasing draw
 // O += texture(iChannel0, u/R);
}
