void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = (fragCoord-0.5*iResolution.xy)/iResolution.y*0.001 +vec2(-0.736,0.143);
    vec2 z = vec2(0,0);
    int t = iFrame;

    for (int x=0;x<t;x++){
        z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y)+c;
        if (length(z)>2.){ fragColor = vec4(vec3(0.0),1.0); break;}
        else {fragColor = vec4(vec3(1.),1.0);}
    }
}
