//an accident
//was exploring how to make fluids using only kernel convolutions
//read my comment here https://www.shadertoy.com/view/fdjGzW
//code applies blur kernel each frame https://en.wikipedia.org/wiki/Kernel_(image_processing)
//but the kernel values are different across space
//the kernel values are the same image its convolving

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0,u)+.5;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float keyA  = texture( iChannel1, vec2(65.5/256.,.25) ).x;
    float keyS  = texture( iChannel1, vec2(83.5/256.,.25) ).x;
    float z = 1.;//kernel convolution size
    vec4 b0 = abs(A(u+vec2(0,0)));
    vec4 b = vec4(0);
    for(float i = -z; i<z+.5;++i){
    for(float j = -z; j<z+.5;++j){
      vec4 s = vec4(0);
      for(float i2 = -z; i2<z+.5;++i2){
      for(float j2 = -z; j2<z+.5;++j2){
      s += abs(A(u+vec2(i,j)+vec2(i2,j2)));
      }}
      if(s.x==0.)s.x = 1.;
      if(s.y==0.)s.y = 1.;
      if(s.z==0.)s.z = 1.;
      if(s.w==0.)s.w = 1.;
      b += b0/s*A(u+vec2(i,j));
    }}
    vec4 a = b;
    if(keyA!=0.)a = texture( iChannel2, u/iResolution.xy)-.5;
    if(keyS!=0.)a = vec4(0);
    if(iFrame==0)a = sin(u.xy*.04+sin(u.yx*.04)).xyxy;
    if(iMouse.z>0.||iFrame==0)
    {
        vec2 m1 = 2.*(u-iMouse.xy)/iResolution.y;
        a += sin(m1.x*32.)/exp(dot(m1,m1)*111.);
    }
    fragColor = a;
}
