precision highp float;

const float PI = 3.14;
const float e = 2.71;
const float PHI = 1.61;

vec2 rotateZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

vec3 draw(vec2 uv, float angle) {
    uv = uv * PI;
    uv = rotateZ(uv, angle);
    float z = (PHI * length(uv));
    vec3 curveColor = vec3(pow(e, -abs(uv.y - z) * 1.));
    return 1. - curveColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    float angle = iTime;

    fragColor = vec4(draw(uv, angle + PI), 1.);
}
