//Example taken from https://www.youtube.com/watch?v=t4XnK50ocMk The Art of Code
vec3 WHITE = vec3(1);
vec3 RED = vec3(178, 34, 52)/255.;
vec3 BLUE = vec3(60, 59, 110)/255.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0);



    if(uv.x > 0.5)
    {
        float y = sin(uv.y*3.1415*13.);
        float w = fwidth(y);
        float stripes = smoothstep(-w, w, y);
        w = fwidth(uv.y);
        col = mix(WHITE, RED, stripes);
    }
    else
    {
        float y = sin(uv.y*3.1415*13.);
        float w = fwidth(y);
        float stripes = smoothstep(-0.1, 0.1, y);
        w = fwidth(uv.y);
        col = mix(WHITE, RED, stripes);
    }

    col *= 1.0 -  step(0.49,uv.x) * step(uv.x,0.51);



    // Output to screen
    fragColor = vec4(col,1.0);
}
