vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = abs(2.*fragCoord-iResolution.xy)/iResolution.y * .5;
    uv = vec2(atan(uv.y,uv.x)/10., length(uv));
    float n = 100.;
    uv.x = abs(uv.x)-.01;
    float id = floor((1.0-uv.x)*n);    
    float t = pow(texture(iChannel0, vec2(1. - id / n, .25)).x,3.);
    t *= max(t-abs(uv.y),-1.)*10.;    
    fragColor.rgb = hsv2rgb(2.*vec3(sin(t*uv.x*10.+iTime),
                                  cos(t*.1+iTime+uv.y/2.-uv.x*50.),
                                  (1.0+cos(iTime*.1*+uv.x))/2.*t*uv.x*10.));
    fragColor.rgb *= max(t,0.) * min(max((length(uv.y)-.01)*10.,0.),1.);
}
