#define MARKER_RADIUS 12.5
#define THICCNESS 10.0

vec2 FC;
vec4 colorOutput;

vec4 marker(vec2 center, vec4 color) 
{
  if ( length(FC.xy - center) < MARKER_RADIUS)
  {
      colorOutput +=color;
  } 

  return colorOutput;
}

vec4 line(vec2 p1, vec2 p2, vec4 color) {
    vec2 p3 = FC.xy;
    vec2 p12 = p2 - p1;
    vec2 p13 = p3 - p1;    

    float d = dot(p12, p13) / length(p12); // = length(p13) * cos(angle)
    vec2 p4 = p1 + normalize(p12) * d;
    if ( length(p4 - p3) < THICCNESS
          && length(p4 - p1) <= length(p12)
          && length(p4 - p2) <= length(p12)) {
        colorOutput +=color;
    }

    return colorOutput;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   vec4 outColor = vec4(0.0);

   vec2 p1 = vec2(200.0, 200.0);
   vec2 p2 = iMouse.xy;
   FC = fragCoord; //ZOZO GLOBAL VARS :P LOL

   outColor+= line(p1, p2, vec4(0.0,0.0,1.0,1.0) );
   outColor+= marker(p1, vec4(1.0,0.0,0.0,1.0) );
   outColor+= marker(p2, vec4(1.0,0.0,0.0,1.0) );
   fragColor = outColor;
}
