void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(1, 0, 0);

    if (uv.y >= 0.5)
        col = vec3(1);

    vec3 overlay = vec3(0, 1, 0);

    if (uv.x > 0.1 && uv.x < 0.3)
        col = 0.5 * col.rgb + 0.5 * overlay; // This is what your favourite art package does

    if (uv.x > 0.4 && uv.x < 0.6)
    {
       // Make a checkerboard effect:
       if ((int(fragCoord.x * 0.5) + int(fragCoord.y * 0.5)) % 2 == 0)
          col = overlay;
	}

    if (uv.x > 0.7 && uv.x < 0.9)
    {
        vec3 linear = pow((col + 0.055) / 1.055, vec3(2.4));
        vec3 linearOverlay = pow((overlay + 0.055) / 1.055, vec3(2.4));

        linear = 0.5 * linear + 0.5 * linearOverlay;
    	col = pow(linear, vec3(1.0 / 2.4)) * 1.055 - 0.055;
    }

    fragColor = vec4(col, 1.0);
}
