void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 colorVec = vec3(0.978, 0.247, 0.201);

    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = fragCoord/iResolution.xy;
    vec2 uv3 = fragCoord/iResolution.xy;
    vec2 uv4 = fragCoord/iResolution.xy;  
    vec2 uv5 = fragCoord/iResolution.xy;

    vec2 pos = vec2(.5, .5);
    vec2 ratio = vec2(0.1, 0.1);
    float distanc = 0.01;
    vec2 offset = uv5 - pos; 
    vec2 rad = offset / ratio;
    float rad2 = sqrt(rad.x*rad.x + rad.y*rad.y);
    float deformation = 1.0 / (pow(rad2 * pow(distanc, 0.5), 2.0) * rad2 * 2.0); 
    offset = offset * (1.0 -  deformation);
    offset += pos;


    uv += mod(iTime * 0.5, 512.0);
    uv2 +=  mod(iTime * 0.8, 512.0);

    uv3.x += mod(iTime * 0.1, 512.0);
    uv3.y -= mod(iTime * 0.1, 512.0);

    uv4.x -= mod(iTime * 0.1, 512.0);
    uv4.y += mod(iTime * 0.1, 512.0);

    float c1 = texture(iChannel0, uv + offset).x;
    float c2 = texture(iChannel1, uv2 + offset + vec2(0.2, 0.0)).x;
    float c3 = texture(iChannel0, uv3 + offset + vec2(0.1, 0.7)).x;
    float c4 = texture(iChannel0, uv4 + offset + vec2(0.4, 0.9)).x;


    vec4 col1 = vec4(c1, c1, c1, 1.0);
    vec4 col2 = vec4(c2, c2, c2, 1.0);
    vec4 col3 = vec4(c3, c3, c3, 1.0);
    vec4 col4 = vec4(c4, c4, c4, 1.0);


    float col = smoothstep(c2 + (c3 - c4), c1 - 1.5 * c2 + (c3 - c4), 0.3);
    col = smoothstep(col, 1.0, 0.81);
    col = 1.0 - col;

    float dist = sqrt(pow((uv5.x-0.5),2.0)+pow((uv5.y-0.5),2.0));


    col = smoothstep(col, 1.0, dist);
    fragColor = vec4(col * colorVec.x, col * colorVec.y, col * colorVec.z, 1.0);
}
