void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    vec2 st = uv - 0.5;

    float timeOffset = iTime*0.1; 
    float d = 1.0 - length(st);
    float theta = atan(st.x, st.y);
    float frequency = 6.0, amplitude = 0.1;

    float sinUv = d + sin(theta * frequency)*amplitude + timeOffset;
    sinUv = mod(sinUv, 10.0);

    // Sample texture
    vec3 col = texture(iChannel0, vec2(sinUv)).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
