#define N 2.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x, y, dx, dy, z;
    float W, H;
    float Nx, Ny;
    float dt;

    x = fragCoord.x;
    y = fragCoord.y;

    W = iResolution.xy.x;
    H = iResolution.xy.y;

    Nx = N;
    Ny = N * (W / H); 

    dy = fract(-iTime/5.0 + abs(y - H / 2.0) * Nx / H);  
    dx = fract(-iTime/5.0 + abs(x - W / 2.0) * Ny / W);  

    dy = fract(dy * 3.0);
    dx = fract(dx * 3.0);

    z = max(dy, dx);
    vec3 col = vec3(z, 0, 0); 
    fragColor = vec4(col, 1.0);
}
