void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0,uv) * vec4(uv.x + (1.-uv.y), 0., (1.-uv.x) + uv.y, 1.);
    fragColor += 0.05*(1.-texture(iChannel0,uv)) * (vec4(uv.x + (1.-uv.y), 1., (1.-uv.x) + uv.y, 0.));
}

bool on(vec2 uv) {
    return (uv.x < 1. && uv.x > 0. && uv.y < 1. && uv.y > 0.);
}
const float none = 0.;
const float hori = 1./3.;
const float vert = 2./3.;
const float dead = 1.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 pixel = 1./iResolution.xy;
    vec2 stick = 2.*pixel;
    fragColor = vec4(texture(iChannel0,uv).r,none,min(1.,texture(iChannel0,uv).r + texture(iChannel0,uv).b),1);

    if(iFrame == 0) {
        fragColor = vec4(0.0,0.0,0.0,1.0);
        if(floor(fragCoord) == floor(iResolution.xy/2.))
            fragColor = vec4(1.0,1./3.,0.0,1.0);
    }

    if(fragColor.b == 0.0) {
    for(float x = -stick.x; x <= stick.x; x += pixel.x)
        if(texture(iChannel0,uv+vec2(x,0)).rgb == vec3(1.0,hori,0.0))
            fragColor = vec4(1.0,((abs(x)==2./iResolution.x)?(fragColor.r == 0.)?vert:1.:0.0),0.0,1.0);
    for(float y = -stick.y; y <= stick.y; y += pixel.y)
        if(texture(iChannel0,uv+vec2(0,y)).rgb == vec3(1.0,vert,0.0))
            fragColor = vec4(1.0,((abs(y)==2./iResolution.y)?(fragColor.r == 0.)?hori:1.:0.0),0.0,1.0);
    }

    if(iMouse.w > -1.) {
        fragColor = vec4(0.0,0.0,0.0,1.0);
        if(floor(fragCoord) == floor(iResolution.xy/2.))
            fragColor = vec4(1.0,1./3.,0.0,1.0);
    }
}
