void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float sinWave = (sin(4.0*3.14*uv.x)*uv.y + 1.0) * 0.1;
    float y = pow(1.0-0.4*uv.y, 6.0) + sinWave + iTime*0.05;

    vec2 offsetUv = vec2(0.5, mod(y, 100.0));
    vec3 col = texture(iChannel0, offsetUv).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
