void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy,
         uv = fragCoord/R.xy,
         st = (fragCoord-.5*R.xy)/R.y;
    float c = sin(iTime - atan(st.x, st.y) * 20.) - sin(iTime - length(st* 2.) * 20.);
    vec3 col = vec3(1. - length(st)) + vec3(c * .8);
    col /= c * 1.5;
    fragColor = vec4(step(.75, col), 1.);    
}
