void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized texture coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    // offset position
    vec2 offset = vec2(0.5*iResolution.x/iResolution.y, 1.0); //middle top

    // mouse
    float mx = iTime/10.0;
    //float mx = iMouse.x / iResolution.x;

    // rotation matrix
    float angle = radians(mx * 360.0);
	uv -= vec2(offset);
	uv *= mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
	uv += vec2(offset);

    // shadow
    vec4 col2 = vec4(smoothstep(0.0, 0.5, distance(uv, offset)));

    // final color
    vec4 col = texture(iChannel0, uv) * col2;
    fragColor = col;
}
