precision highp float;

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

float sphere(vec3 p,float s){
  return length(p*vec3(1.,1.,3.))-s;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 r = iResolution.xy;
    vec4 FC = gl_FragCoord;
    float t = iTime * 0.3;
    vec4 o = vec4(0.);

    float F = .5 * 10.;

    vec3 p=vec3((FC.xyz*2.-r.xyx)/r.xyx),q=p;
    p=normalize(p);
    //q.x -= cos(t*.5+1.) * 2.;
    q.x -= t * 2.;
    q.y -= t*.3 * 2.;

    for(float i,d,e,c;i++<50.;){
        c=e=1.;
        q+=(e=+d)*p;
        vec3 mq = vec3(0);

        mq.x = mod(q.x+p.z*5.+sin(t*.3),2.)-1.;
        mq.y = mod(q.y+p.z*F-t*1.3,2.)-1.;
        mq.z += mod(q.z*.3, 2.)-1.; 
        d=sphere(mq, 0.503);
        d=max(-d,sphere(mq, 0.5));

        mq.x = mod(q.x+cos(t*.7),4.)-2.;
        mq.y = mod(q.y+p.z-t,4.)-2.;
        mq.z += mod(q.z*.3, 3.)-1.5; 
        c=sphere(mq-.7, 1.001);
        d=min(d,max(-c,sphere(mq-.7, 1.)));

        o+=vec4(hsv(q.z+q.x,.8,0.05/pow(3.,q.z*q.z*.003+i*e)),1.);
    }


    fragColor = o;
}

