#define PI 3.14159
#define TWO_PI 6.28318
#define HALF_PI 1.57079

vec3 hue(float val) {
    vec3 col;
    float h = mod(val, 1.0);
    float X = -abs(6.0 * mod(h, 1.0 / 3.0) - 1.0) + 1.0;

    if(h < 1.0 / 6.0)
        col = vec3(1.0,X,0.0);
    else if(h < 2.0 / 6.0)
        col = vec3(X,1.0,0.0);
    else if(h < 3.0 / 6.0)
        col = vec3(0.0,1.0,X);
    else if(h < 4.0 / 6.0)
        col = vec3(0.0,X,1.0);
    else if(h < 5.0 / 6.0)
        col = vec3(X,0.0,1.0);
    else
        col = vec3(1.0,0.0,X);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;


    float dist = distance(uv, iMouse.xy);

    float feather = 2.;
    float inside = 45.;
    float outside = 50.;

    vec3 col = vec3((
    // inner circle
    (1.0f - smoothstep(inside, inside - feather, dist))*
    // outer circle
    smoothstep(outside, outside - feather, dist)))*
    // colour :)
    hue(mod(iTime / 3.0f + ((atan(uv.y - iMouse.y, uv.x - iMouse.x) + PI) / TWO_PI), 1.0f));

    fragColor = vec4(col,1.0);
}
