float weirdsmin(float a, float b, float k){
    return abs(a*b)-k;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec2 c1 = vec2(0.5,0.5);
    vec2 c2 = iMouse.xy/iResolution.y;
    if (iMouse.z <= 0.0){
        c2 = vec2(cos(iTime)/2.0,sin(iTime)/4.0) + vec2(1.0,0.5);
    }

    vec3 col = vec3(0);
    col = mix(col, vec3(1), smoothstep(0.0,0.01,weirdsmin(distance(c1, uv)-0.3, distance(c2, uv)-0.2, 0.001)));

    fragColor = vec4(col,1.0);
}
