void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec4 color = vec4(0.5,  0.5,   1.0,  1.0);

    vec2 center = iMouse.xy;
    float radius = 200.0;
    float smoothR = 1.0; // anitialiasing radius

    float d = distance(center, fragCoord);
    fragColor = color * (1.0 - smoothstep(radius - smoothR / 2.0, radius + smoothR / 2.0, d));
}
