void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = texture( iChannel1, fragCoord/iResolution.xy );
    vec2 r =fragCoord.xy/iResolution.xy;

    float dotSize = 5.0*(1.0+sin(2.0*iTime));

    for(float i=5.0; i<800.0; i=i+18.0){
    if(fragCoord.x < i+dotSize && fragCoord.x>i-dotSize){
    for(float j=0.0;j<550.0;j=j+18.0){
    if(fragCoord.y < j+dotSize && fragCoord.y > j-dotSize){
    vec3 re1=vec3((color.r+color.b+color.g)/3.0)*vec3(r.x, abs(cos(iTime)), abs(sin(2.0*iTime)));
    color = vec4(re1,1.0);
    }
    }
    }
    }
	fragColor = color;
}
