void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    if(iFrame == 0){
        if(fragCoord.x < float(num) && floor(fragCoord.y) == float(row)){
            fragColor = vec4(hash3(uv), hash3(uv.yx), 0., 0.);
