#define row 5
#define num 100

float hash3(vec2 xy){
    xy = mod(xy, .19);
    float h = dot(xy.yyx,vec3(.013, 27.15, 2027.3));
    h *= h;
    h *= fract(h);

    return fract(h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    ivec2 fragI = ivec2(int(fragCoord.x), int(fragCoord.y));

    vec4 col = texelFetch(iChannel1, fragI, 0);


    if(iFrame == 0){
        float closest = 1.5;

        for(int i = 0; i < num; i++){

            vec4 p = texelFetch(iChannel0, ivec2(i, row), 0);

            if(distance(uv, p.xy) < closest){
                closest = distance(uv, p.xy);
                col = p;//vec4(1. / (closest * 100.0));
            }
        }
    }


    fragColor = col;


}
