float r(vec2 p) { return smoothstep(1.,.99,length(p)); }

void mainImage( out vec4 O, in vec2 I )
{
    vec2 p = (2.*I - iResolution.xy) / iResolution.y; 
    p -= iTime/20.;

    vec4 c = vec4(1.73,.87,.5,-.87);

	p.y *= c.x;
	p = fract(p);
	p.x *= c.x;
	p -= c.yz;

	vec2 o = p * r(p);
	o = mix(o, p+c.yz, r(p+c.yz));
    o = mix(o, p+c.wz, r(p+c.wz));
    o = mix(o, p+vec2(0,1), r(p+vec2(0,1)));

    O = vec4(o,0,0);
}
