vec4 number1(vec2 fragCoord)
{
    float screen1WidthPercent = .5;
    if(fragCoord.x<iResolution.x * screen1WidthPercent)
    {
        return vec4(1,0,0,1.0);
    }
    return vec4(0,1,0,1.0);
}

vec4 number2 (vec2 fragCoord)
{
    vec4 color = vec4(0,0,1,1.0);
    if(fragCoord.y>=49.0 && fragCoord.y <=51.0)
        return vec4(1,0,0,1.0);
    return color;
}

vec4 number3 (vec2 fragCoord)
{
    vec4 brighter = vec4(1.1,1.1,1.1,1.0);
    vec2 uv = fragCoord/iResolution.xy;
    vec4 texColor =texture(iChannel0,uv);
    texColor = texColor*brighter;
    return texColor;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragColor = number1(fragCoord);
    //fragColor = number2(fragCoord);
    //fragColor = number3(fragCoord);
}
